(171, 257, 'Ouvidoria', '', '<h2 class="contentheading" style="font-size: 1.8em; color: #4c94b7; font-family: Arial; margin: 0px 0px 0.25em; font-weight: bold; letter-spacing: -1px;">Ouvidoria CTB</h2>\r\n<p style="margin: 0.35em 0px 0.8em; padding: 0px; color: #666666; font-family: Arial; font-size: 12px; line-height: 18px;"> </p>\r\n<p style="margin: 0.35em 0px 0.8em; padding: 0px; color: #666666; font-family: Arial; font-size: 12px; line-height: 18px;"><a href="http://www.tag2.ouvidoriageral.ba.gov.br/CTB" target="_blank"><img src="images/CentraldeAtendimentoFerrovia.png" alt="" width="441" height="172" /></a></p>\r\n<p style="margin: 0.35em 0px 0.8em; padding: 0px; color: #666666; font-family: Arial; font-size: 12px; line-height: 18px;"> </p>\r\n<p style="margin: 0.35em 0px 0.8em; padding: 0px; color: #666666; font-family: Arial; font-size: 12px; line-height: 18px;"><img src="images/CentraldeAtendimento.png" alt="" width="410" height="328" /></p>', 1, 'user1', 0, '0000-00-00 00:00:00', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 1, 'mod_custom', 1, 0, '{"prepare_content":"0","backgroundimage":"","layout":"_:default","moduleclass_sfx":"","cache":"1","cache_time":"900","cachemode":"static","module_tag":"div","bootstrap_size":"0","header_tag":"h3","header_class":"","style":"0"}', 0, '*'),
(172, 260, 'Relação de Siglas e Setores da CTB', '', '<h2 class="contentheading">Relação de Siglas e Setores da CTB</h2>\r\n<p> </p>\r\n<p><strong>DIPRE</strong> - Diretoria de Presidência</p>\r\n<p><strong>ASJUR</strong> - Assessoria Jurídica</p>\r\n<p><strong>ASTEC</strong> - Assessoria Técnica</p>\r\n<p><strong>ASREI</strong> - Assessoria de Relações Institucionais</p>\r\n<p> </p>\r\n<p><strong>DIRAF</strong> - Diretoria Administrativa Financeira</p>\r\n<p><strong>CODGE</strong> - Coordenadoria de Desenvolvimento da Gestão</p>\r\n<p><strong>QUAS</strong> - Subcoordenadoria de Gestão de Qualidade e Serviços</p>\r\n<p><strong>TECI</strong> - Subcoordenadoria de Tecnologia da Informação</p>\r\n<p><strong>GESP</strong> - Subcoordenadoria de Gestão de Pessoas</p>\r\n<p><strong>COADM</strong> - Coordenadoria Administrativa</p>\r\n<p><strong>SPAT</strong> - Subcoordenadoria de Suprimentos e Patrimônio</p>\r\n<p><strong>GSER</strong> - Subcoordenadoria de Gestão de Serviços</p>\r\n<p><strong>COFIN</strong> - Coordenadoria Financeira</p>\r\n<p><strong>CEFI</strong> - Subcoordenadoria de Controle e Execução Financeira</p>\r\n<p><strong>FATC</strong> - Subcoordenadoria de Faturamento e Cobrança</p>\r\n<p><strong>CONT</strong> - Subcoordenadoria de Contabilidade</p>\r\n<p><strong>CONV</strong> - Subcoordenadoria de Convênios e Contratos</p>\r\n<p> </p>\r\n<p><strong>DIROM</strong> - Diretoria de Operação e Manutenção</p>\r\n<p><strong>COMAM</strong> - Coordenadoria de Manutenção</p>\r\n<p><strong>MARO</strong> - Subcoordenadoria de Material Rodante</p>\r\n<p><strong>SISF</strong> - Subcoordenadoria de Sistema Fixo</p>\r\n<p><strong>VIAP</strong> - Subcoordenadoria de Via Permanente</p>\r\n<p><strong>COOPE</strong> - Coordenadoria de Operação</p>\r\n<p><strong>TMOV</strong> - Subcoordenadoria de Trens, COntrole e Movimento</p>\r\n<p><strong>POPE</strong> - Subcoordenadoria de Planejamento Operacional das Estações</p>\r\n<p> </p>\r\n<p><strong>DIRES</strong> - Diretoria de Estações</p>\r\n<p><strong>COBEU</strong> - Coordenadoria do Bem Estar do Usuário</p>\r\n<p><strong>SEGU</strong> - Subcoordenadoria de Segurança dos Usuários</p>\r\n<p><strong>COSEG</strong> - Coordenadoria de Segurança</p>\r\n<p><strong>SEPA</strong> - Subcoordenadoria de Segurança Patrimonial</p>\r\n<p> </p>\r\n<p><strong>DIPLA</strong> - Diretoria de Planejamento</p>\r\n<p><strong>COAAP</strong> - Coordenadoria de Avaliação e Acompanhamento de Projetos</p>\r\n<p><strong>COESP</strong> - Coordenadoria de Estudos e Planos</p>\r\n<p> </p>\r\n<p><strong>DIROB</strong> - Diretoria de Obras</p>\r\n<p><strong>COOES</strong> - Coordenadoria de Operações Especiais</p>\r\n<p><strong>COFIS</strong> - Coordenadoria de Fiscalização de Obras</p>\r\n<p> </p>', 1, 'user1', 303, '2015-04-22 16:57:10', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 1, 'mod_custom', 1, 0, '{"prepare_content":"0","backgroundimage":"","layout":"_:default","moduleclass_sfx":"","cache":"1","cache_time":"900","cachemode":"static","module_tag":"div","bootstrap_size":"0","header_tag":"h3","header_class":"","style":"0"}', 0, '*'),
(173, 262, 'Comprasnet', '', '<p> </p>\r\n<p><a href="http://www.comprasnet.ba.gov.br/" target="_blank"><img src="images/banners/banner-184.jpg" alt="" width="200" height="54" /></a></p>\r\n<p><a href="http://www.ba.gov.br/modules/conteudo/conteudo.php?conteudo=18" target="_blank"><img src="http://www.ba.gov.br/arquivos/Image/CONHECA_O_GOVERNO/acesso_informacao.jpg" alt="" width="200" height="65" /></a></p>\r\n<p><a href="http://sistemas.sefaz.ba.gov.br/sistemas/tbweb/modulos/home/main.aspx" target="_blank"><img src="http://www.ba.gov.br/arquivos/Image/PROGRAMAS/selo_transparencia.jpg" alt="" width="195" height="60" /></a></p>', 1, 'right', 0, '0000-00-00 00:00:00', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 1, 'mod_custom', 1, 0, '{"prepare_content":"0","backgroundimage":"","layout":"_:default","moduleclass_sfx":"","cache":"1","cache_time":"900","cachemode":"static","module_tag":"div","bootstrap_size":"0","header_tag":"h3","header_class":"","style":"0"}', 0, '*');

